    public static boolean ValidateDecimalsOnly(String inputString)
    {
        return decimalsOnlyPattern.matcher(inputString).matches();
    }

    public static boolean ValidateLettersOnly(String inputString)
    {
        return lettersOnlyPattern.matcher(inputString).matches();
    }

    public static boolean ValidateLettersAndSpacesOnly(String inputString)
    {
        return lettersAndSpacesOnlyPattern.matcher(inputString).matches();
    }

    public static boolean ValidateLettersAndNumbersOnly(String inputString)
    {
        return lettersAndNumbersOnlyPattern.matcher(inputString).matches();
    }

    public static boolean ValidateNIC(String nic)
    {
        return nicPattern.matcher(nic).matches();
    }

    public static boolean ValidatePhoneNo(String phoneNo)
    {
        return phoneNoPattern.matcher(phoneNo).matches();
    }

    public static boolean ValidateEmail(String emailAddress)
    {
        return emailPattern.matcher(emailAddress).matches();
    }

